mapper.select:
select * 
from ( select rownum rnum, cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like
	   from (
			  select cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like
			  from comments where isbn = #{isbn}			   
	  		   order by cmt_date desc
			 )		   
   )
   where rnum >=#{start} and rnum <= #{end}

select * 
from ( select rownum rnum, cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like
	   from (
			  select cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like
			  from comments where isbn = #{isbn}			   
	  		   order by cmt_like desc
			 )		   
   )
   where rnum >=#{start} and rnum <= #{end}

select * 
from ( select rownum rnum, cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like
	   from (
			  select cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like
			  from comments where isbn = #{isbn} and cmt_id = #{id}		   
	  		   order by cmt_like desc
			 )		   
   )
   where rnum >=#{start} and rnum <= #{end}

select * 
from ( select rownum rnum, cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like, isbn
	   from (
			  select cmt_no, cmt_nickname, cmt_content, cmt_date, cmt_mbti, cmt_id, cmt_like, isbn
			  from comments where cmt_id=#{id}
			 )		   
   )
   where rnum >=#{start} and rnum <= #{end}
